targets.expr:
(
  sum without(instance, node) (
    topk(
      1,
      kubelet_volume_stats_capacity_bytes{
        cluster=~"$cluster",
        job="kubelet",
        metrics_path="/metrics",
        namespace=~"$namespace",
        persistentvolumeclaim=~"${volume:regex}"
      }
    )
  )
  -
  sum without(instance, node) (
    topk(
      1,
      kubelet_volume_stats_available_bytes{
        cluster=~"$cluster",
        job="kubelet",
        metrics_path="/metrics",
        namespace=~"$namespace",
        persistentvolumeclaim=~"${volume:regex}"
      }
    )
  )
)

(
  node_memory_MemTotal_bytes{job="node-exporter", instance="$node", cluster="$cluster"}
-
  node_memory_MemFree_bytes{job="node-exporter", instance="$node", cluster="$cluster"}
-
  node_memory_Buffers_bytes{job="node-exporter", instance="$node", cluster="$cluster"}
-
  node_memory_Cached_bytes{job="node-exporter", instance="$node", cluster="$cluster"}
)
